    private void solveButtonActionPerformed(java.awt.event.ActionEvent evt) {                                            
        if(depthSearch.isSelected())
            problem.breadthFirstSearch((Vertex) problem.getCurrentState());
        else
            problem.depthFirstSearch((Vertex) problem.getCurrentState());
        
        solLengthStat.setText(problem.getQueueSize()+"");
        numDEQUEStat.setText(problem.getQueueOps()+"");
        maxSizeStat.setText(problem.getMaxQueueSize()+"");
        
    }                                           

