    private void depthSearchActionPerformed(java.awt.event.ActionEvent evt) {                                            
        if(breadthSearch.isSelected() == true){
            breadthSearch.setSelected(false);
        }
    }                                           

    private void breadthSearchActionPerformed(java.awt.event.ActionEvent evt) {                                              
        if(depthSearch.isSelected() == true){
            depthSearch.setSelected(false);
        }
    }                                             


